    private void btnprosesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnprosesActionPerformed
        String jenkel = " ", bulan, negara;
        int tahun, tanggal, usiathn;
        if (btnlaki.isSelected()) {
            jenkel = "Laki-laki";
        } else if (btnprmpuan.isSelected()) {
            jenkel = "Perempuan";
        }
        tanggal = Integer.parseInt((String) boxtgl.getSelectedItem());
        bulan = boxbln.getSelectedItem().toString();
        tahun = Integer.parseInt((String) boxthn.getSelectedItem());
        String nama = txtnama.getText();
        String alamat = txtalamat.getText();
        String hp = txtnoHP.getText();
        String hobby = txthobby.getText();
        String tempat = txttempat.getText();
        String output = "=============DATA DIRI=============\n"
                + "NAMA :  " + nama
                + "\nTempat/Tanggal Lahir :  " + tempat + " / " + tanggal + "-" + bulan + "-" + tahun
                + "\nJenis Kelamin :  " + jenkel
                + "\nAlamat :  " + alamat
                + "\nNomor Handphone :  " + hp
                + "\nHobby :  " + hobby;
        outhsl.setText(output);
        JOptionPane.showMessageDialog(null, output);
    }//GEN-LAST:event_btnprosesActionPerformed


    public String[] reset() {
        txtnama.setText(" ");
        txttempat.setText(" ");
        txtalamat.setText(" ");
        txtnoHP.setText(" ");
        txthobby.setText(" ");
        boxtgl.setSelectedItem("Tanggal");
        boxbln.setSelectedItem("Bulan");
        boxthn.setSelectedItem("Tahun");
        boxnegara.setSelectedItem("Negara");
        outhsl.setText("");
        txtzodiak.setText(" ");
        txtumur.setText(" ");
        String[] result = new String[8];
        result[0] = txtnama.getText();
        result[1] = txttempat.getText();
        result[2] = txtalamat.getText();
        result[3] = txtnoHP.getText();
        result[4] = txthobby.getText();
        result[5] = outhsl.getText();
        result[6] = txtzodiak.getText();
        result[7] = txtumur.getText();
        return result;
    }

