    public String zodiak(int thn, int cTahun, int tanggal, int bulan) {

        umur = thn - cTahun;
        txtumur.setText(String.valueOf(umur));

        if ((tanggal >= 21 && bulan == 3) || (tanggal <= 19 && bulan == 4)) {
            txtzodiak.setText(String.valueOf("Aries"));
            return txtzodiak.getText();
        } else if ((tanggal >= 20 && bulan == 4) || (tanggal <= 20 && bulan == 5)) {
            txtzodiak.setText(String.valueOf("Taurus"));
            return txtzodiak.getText();
        } else if ((tanggal >= 21 && bulan == 5) || (tanggal <= 20 && bulan == 6)) {
            txtzodiak.setText(String.valueOf("Gemini"));
            return txtzodiak.getText();
        } else if ((tanggal >= 21 && bulan == 6) || (tanggal <= 22 && bulan == 7)) {
            txtzodiak.setText(String.valueOf("Cancer"));
            return txtzodiak.getText();
        } else if ((tanggal >= 23 && bulan == 7) || (tanggal <= 22 && bulan == 8)) {
            txtzodiak.setText(String.valueOf("Leo"));
            return txtzodiak.getText();
        } else if ((tanggal >= 23 && bulan == 8) || (tanggal <= 22 && bulan == 9)) {
            txtzodiak.setText(String.valueOf("Virgo"));
            return txtzodiak.getText();
        } else if ((tanggal >= 23 && bulan == 9) || (tanggal <= 22 && bulan == 10)) {
            txtzodiak.setText(String.valueOf("Libra"));
            return txtzodiak.getText();
        } else if ((tanggal >= 23 && bulan == 10) || (tanggal <= 21 && bulan == 11)) {
            txtzodiak.setText(String.valueOf("Scorpio"));
            return txtzodiak.getText();
        } else if ((tanggal >= 22 && bulan == 11) || (tanggal <= 21 && bulan == 12)) {
            txtzodiak.setText(String.valueOf("Sagitarius"));
            return txtzodiak.getText();
        } else if ((tanggal >= 22 && bulan == 12) || (tanggal <= 19 && bulan == 1)) {
            txtzodiak.setText(String.valueOf("Capricorn"));
            return txtzodiak.getText();
        } else if ((tanggal >= 20 && bulan == 1) || (tanggal <= 18 && bulan == 2)) {
            txtzodiak.setText(String.valueOf("Aquarius"));
            return txtzodiak.getText();
        } else if ((tanggal >= 19 && bulan == 2) || (tanggal <= 20 && bulan == 3)) {
            txtzodiak.setText(String.valueOf("Pisces"));;
            return txtzodiak.getText();
        }
        return null;
    }

